import React, {useRef} from 'react';
import {Form , Col, Button} from 'react-bootstrap';
import { Formik, Field } from 'formik';
import * as Yup from 'yup';

const loginSchema = Yup.object().shape({
    username: Yup.string()
        .required('Required'),
    password: Yup.string()
        .min(2, 'Password must be 2 characters at minimum.')
        .max(10, 'Password must be 10 characters at maximum.')
        .required('Required'),
});

const LoginPage = () => {
    const formik = useRef(null);



    return (
        <div className="global-container">
            <div className="col-md-4 container-login p-5">
                <img className="my-4" width="100px" src="/images/icon-person.png" alt="Icon Person"/>
                <div className="form-style">
                    <Formik
                        ref={formik}
                        initialValues={ {
                                    email: '',
                                    password: '',
                                }
                        }
                        validationSchema={loginSchema}
                        onSubmit={(values, actions) => {
                            setTimeout(() => {
                                // alert(JSON.stringify(values, null, 2));
                                // if ('name' in values) registerLocalUser(values);
                                // else loginLocalUser(values);
                                // actions.setSubmitting(false);
                                // actions.resetForm();
                                // redirectAfterLogin(() => history.push('/home'));
                            }, 1000);
                        }}
                        onChange={e => console.log(e)}
                        render={props => {
                            const {
                                handleSubmit,
                                handleChange,
                                handleBlur,
                                values,
                                touched,
                                isValid,
                                errors,
                                isSubmitting,
                            } = props;
                            return (
                                <>
                                    <Form noValidate onSubmit={handleSubmit}>
                                        <Form.Group controlId="validationFormik2">
                                            <Form.Control
                                                name="email"
                                                type="email"
                                                placeholder="Email"
                                                value={values.email}
                                                onChange={handleChange}
                                                onBlur={handleBlur}
                                                isInvalid={touched.email && !!errors.email}
                                                isValid={touched.email && !errors.email}
                                            />
                                            <Form.Control.Feedback>
                                                Looks good!
                                            </Form.Control.Feedback>
                                            <Form.Control.Feedback type="invalid">
                                                {errors.email}
                                            </Form.Control.Feedback>
                                        </Form.Group>
                                        <Form.Group controlId="validationFormik3">
                                            <Form.Control
                                                name="password"
                                                type="password"
                                                placeholder="Password"
                                                value={values.password}
                                                onChange={handleChange}
                                                onBlur={handleBlur}
                                                isInvalid={touched.password && !!errors.password}
                                                isValid={touched.password && !errors.password}
                                            />
                                            <Form.Control.Feedback>
                                                Looks good!
                                            </Form.Control.Feedback>
                                            <Form.Control.Feedback type="invalid">
                                                {errors.password}
                                            </Form.Control.Feedback>
                                        </Form.Group>
                                        <Form.Row>
                                            <Form.Group
                                                as={Col}
                                                md="6"
                                                controlId="validationFormik3"
                                            >
                                                <Form.Group>
                                                    <Button
                                                        type="submit"
                                                        className="btn-block"
                                                        disabled={isSubmitting}
                                                    >
                                                        Login
                                                    </Button>
                                                </Form.Group>
                                            </Form.Group>
                                            <div className="col-md-6 text-right">
                                                <a className="small" href="#">
                                                    Forgot password?
                                                </a>
                                            </div>
                                        </Form.Row>

                                    </Form>
                                    {/* <DisplayFormikState {...props} /> */}
                                </>
                            );
                        }}
                    />
                    {/*<form autoComplete="off">*/}
                    {/*    <div className="input-group mb-3">*/}
                    {/*        <div className="input-group-prepend">*/}
                    {/*            <span className="input-group-text"><i className="bi bi-person-fill"/></span>*/}
                    {/*        </div>*/}
                    {/*        <input type="text" className="form-control" placeholder="Username" aria-label="Username"/>*/}
                    {/*    </div>*/}
                    {/*    <div className="input-group mb-4">*/}
                    {/*        <div className="input-group-prepend">*/}
                    {/*            <span className="input-group-text"><i className="bi bi-lock-fill"/></span>*/}
                    {/*        </div>*/}
                    {/*        <input type="password" className="form-control" placeholder="Password"*/}
                    {/*               aria-label="Password"/>*/}
                    {/*    </div>*/}
                    {/*    <div className="d-flex align-items-center justify-content-between">*/}
                    {/*        <div className="d-flex align-items-center">*/}
                    {/*            <input name="" type="checkbox" value=""/>*/}
                    {/*            <span className="mx-2 font-weight-bold text-white">Remember Me</span>*/}
                    {/*        </div>*/}
                    {/*        <div className="fst-italic text-decoration-underline text-white">Forget Password?</div>*/}
                    {/*    </div>*/}
                    {/*    <div>*/}
                    {/*        <button type="submit" className="btn btn-sm btn-dark button-submit px-5 mt-4">LOGIN</button>*/}
                    {/*    </div>*/}
                    {/*    <div className="my-3 text-white ">*/}
                    {/*        <span>Belum memiliki akun ? <i className="fst-italic text-decoration-underline text-white"> Daftar disini</i>*/}
                    {/*    </span>*/}
                    {/*    </div>*/}

                    {/*</form>*/}
                </div>
            </div>
        </div>
    )
}

export default LoginPage;
