import React, {useRef} from "react";
import {Formik} from "formik";
import * as yup from "yup";
import {Button, Col, Container, Form, InputGroup, Navbar, NavDropdown, Row} from "react-bootstrap";
import {getAuth, updatePassword} from "firebase/auth";
import emailjs from '@emailjs/browser'
import FormTextField from "../components/form/form";
import {useNavigate} from "react-router-dom";

const forgotSchema = yup.object().shape({
    email: yup.string().email("Email tidak sesuai format!").required('Email harus di isi!'),
});

const ChangePassword = () => {
    const form = useRef();
    const serviceId = 'service_edkqgll'
    const templateId = 'template_zcvklq5'
    const userId = 'yl3YSJ8TXme_4FreF'

    const handleSubmit = (e) => {

        e.preventDefault()
        emailjs.sendForm(serviceId, templateId, form.current, userId).then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
    }

    return (
        <div>
            <div className="global-container">
                <div className="col-md-4 container-login p-5">
                    <h4 className="text-white">Ubah Password</h4>
                    <Row className="mt-5">
                        <Col>
                            <form ref={form} onSubmit={(e) => handleSubmit(e)}>
                                <Form.Group>
                                    <InputGroup>
                                        <InputGroup.Text><i className="bi bi-person-fill"/></InputGroup.Text>
                                        <Form.Control
                                            id="email"
                                            name= "email"
                                            type="email"
                                            placeholder="Email"
                                        />
                                    </InputGroup>
                                </Form.Group>
                                <input type="submit" value="Send"/>
                            </form>
                        </Col>
                    </Row>
                </div>
            </div>
        </div>

    );
}

export default ChangePassword;
