import React from "react";
import {Formik} from "formik";
import * as yup from "yup";
import emailjs from 'emailjs-com'
import {Button, Col, Container, Form, Navbar, NavDropdown, Row} from "react-bootstrap";

import FormTextField from "../components/form/form";
import {useNavigate} from "react-router-dom";

const forgotSchema = yup.object().shape({
    email: yup.string().email("Email tidak sesuai format!").required('Email harus di isi!'),
});

const ChangePassword = () => {
    const navigate = useNavigate();
    const serviceId = 'service_edkqgll';
    const templateId = 'template_zcvklq5';
    const userId = 'yl3YSJ8TXme_4FreF';

    const handleSubmit = (values) => {
        const email = values.email;
        emailjs.sendForm(serviceId, templateId, email, userId)
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
    }

    return (
        <div>
            <div className="global-container">
                <div className="col-md-4 container-login p-5">
                    <h4 className="text-white">Ubah Password</h4>
                    <Row className="mt-5">
                        <Col>
                            <Formik
                                validationSchema={forgotSchema}
                                onSubmit={(values) => handleSubmit(values)}
                                initialValues={{
                                    firstName: '',
                                    password: '',
                                }}
                            >
                                {({
                                      handleSubmit,
                                      isValid,
                                  }) => (
                                    <Form noValidate onSubmit={handleSubmit}>
                                        <Col>
                                            <FormTextField
                                                controlId="validationFormEmail"
                                                label="Email"
                                                type="email"
                                                name="email"
                                            />
                                        </Col>
                                        <Col className="my-4">
                                            <Button
                                                disabled={!isValid}
                                                as="input"
                                                className="button-submit px-5"
                                                size="lg"
                                                type="submit"
                                                value="Ubah Password"
                                            />
                                        </Col>

                                    </Form>
                                )}
                            </Formik>
                        </Col>
                    </Row>
                </div>
            </div>
        </div>

    );
}

export default ChangePassword;
