import React, { useState, useRef } from 'react';
import { Formik, Field } from 'formik';
import * as Yup from 'yup';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';


const loginSchema = Yup.object().shape({
    email: Yup.string()
        .email('Invalid email')
        .required('Required'),
    password: Yup.string()
        .min(2, 'Password must be 2 characters at minimum.')
        .max(10, 'Password must be 10 characters at maximum.')
        .required('Required'),
});

const Login = () => {

    return (
        <Row>
            <Col xs={12} sm={8} md={6} lg={4} className="mx-auto mt-5">
                <div className="card">
                    <Formik
                        initialValues={{ username: '', password: '' }}
                        validationSchema={loginSchema}
                        validateOnMount
                        onSubmit={(values) => {
                        }}
                    >
                        {({ handleSubmit, errors, touched }) => (
                            <form onSubmit={handleSubmit}>
                                <VStack spacing={6} align="flex-start">
                                    <FormControl>
                                        <Field id="username" name="username" type="text" autoFocus>
                                            {({ field }: any) => (
                                                <InputGroup>
                                                    <InputLeftElement pointerEvents="none">
                                                        <IconMail color="#B6B6B6" size={20} />
                                                    </InputLeftElement>
                                                    <Input
                                                        className={errors.username && touched.username ? 'input-error' : null}
                                                        size="lg"
                                                        {...field}
                                                        placeholder="Email atau Kode Mitra"
                                                    />
                                                </InputGroup>
                                            )}
                                        </Field>
                                        <ErrorMessage name="username" component="span" className="error-message" />
                                    </FormControl>
                                    <FormControl>
                                        <InputPassword id="password" name="password" placeholder="Password" touched={touched} errors={errors} />
                                        <ErrorMessage name="password" component="span" className="error-message" />
                                    </FormControl>
                                </VStack>
                                <Flex direction="column" align="flex-end" w="full" mt="3">
                                    <Button
                                        variant="link"
                                        className={style.linkTo}
                                        onClick={() => navigate('/forgot-password')}
                                        size="sm"
                                        fontWeight={400}
                                    >
                                        Lupa Password?
                                    </Button>
                                </Flex>
                                <Flex direction="column" align="center" gap={4} w="full" mt="8">
                                    <Button
                                        colorScheme="primary"
                                        size="md"
                                        w="full"
                                        fontSize={14}
                                        type="submit"
                                        disabled={!!errors.password || !!errors.username || loading}
                                        isLoading={loading}
                                    >
                                        Masuk
                                    </Button>
                                </Flex>
                            </form>
                        )}
                    </Formik>
                </div>
            </Col>
        </Row>
    );
};

export default Login;
