import React from "react";
import {Formik} from "formik";
import * as yup from "yup";
import {Button, Col, Form, Row, InputGroup} from "react-bootstrap";
import FormTextField from "../components/form/form";

const loginSchema = tup.object().shape({
    username: yup.string().required('Email harus di isi!'),
    password: yup.string()
        .min(8, 'Password must be 2 characters at minimum.')
        .max(10, 'Password must be 10 characters at maximum.')
        .required('Required'),
});

const handleChange = () => {
    console.log("n")
}

const Input = () => {
    return (
        <InputGroup.Prepend>
            <InputGroup.Text id="inputGroupPrepend">@</InputGroup.Text>
        </InputGroup.Prepend>
    )
}

const Login = () => {
    return (
        <div className="global-container">
            <div className="col-md-4 container-login p-5">
                <img className="my-2" width="100px" src="/images/icon-person.png" alt="Icon Person"/>
                <h3 className="text-white my-4">Daftar</h3>
                <Row>
                    <Col>
                        <Formik
                            validationSchema={loginSchema}
                            onSubmit={handleChange}
                            initialValues={{
                                firstName: '',
                                password: '',
                            }}
                        >
                            {({
                                  handleSubmit,
                                  isValid,
                                  isSubmitting
                              }) => (
                                <Form noValidate onSubmit={handleSubmit}>
                                    <Col>
                                        <FormTextField
                                            controlId="validationFormikUsername"
                                            label="Username"
                                            type="text"
                                            name="username"
                                        />
                                    </Col>
                                    <Col>
                                        <FormTextField
                                            controlId="validationFormikUsername"
                                            label="Password"
                                            type="password"
                                            name="password"
                                        />
                                    </Col>
                                    <Col>
                                        <Button
                                            disabled={!isValid || isSubmitting}
                                            variant="success"
                                            as="input"
                                            size="lg"
                                            type="submit"
                                            value="Submit"
                                        />
                                    </Col>
                                </Form>
                            )}
                        </Formik>
                    </Col>
                </Row>
            </div>
        </div>
    );
}

export default Login;
