import React, { useState, useRef } from 'react';
import { Formik, Field } from 'formik';
import * as Yup from 'yup';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import {FormControl, Form} from "react-bootstrap";
import InputGroup from 'react-bootstrap/InputGroup';


const loginSchema = Yup.object().shape({
    email: Yup.string()
        .email('Invalid email')
        .required('Required'),
    password: Yup.string()
        .min(2, 'Password must be 2 characters at minimum.')
        .max(10, 'Password must be 10 characters at maximum.')
        .required('Required'),
});

const Login = () => {

    return (
        <Row>
            <Col xs={12} sm={8} md={6} lg={4} className="mx-auto mt-5">
                <div className="card">
                    <Formik
                        initialValues={{ username: '', password: '' }}
                        validationSchema={loginSchema}
                        validateOnMount
                        onSubmit={(values) => {
                        }}
                    >
                        {({ handleSubmit, errors, touched }) => (
                            <form onSubmit={handleSubmit}>
                                    <FormControl>
                                        <Field id="username" name="username" type="text" autoFocus>
                                            {({ field }) => (
                                                <InputGroup size="sm" className="mb-3">
                                                    <InputGroup.Text id="inputGroup-sizing-sm">Small</InputGroup.Text>
                                                    <Form.Control
                                                        aria-label="Small"
                                                        aria-describedby="inputGroup-sizing-sm"
                                                    />
                                                </InputGroup>
                                            )}
                                        </Field>
                                    </FormControl>
                                    <FormControl>
                                        <Field id="password" name="password" type="text" autoFocus>
                                            {({ field }) => (
                                                <InputGroup size="sm" className="mb-3">
                                                    <InputGroup.Text id="inputGroup-sizing-sm">Small</InputGroup.Text>
                                                    <Form.Control
                                                        aria-label="Small"
                                                        aria-describedby="inputGroup-sizing-sm"
                                                    />
                                                </InputGroup>
                                            )}
                                        </Field>
                                    </FormControl>
                                    {/*<Button*/}
                                    {/*    variant="link"*/}
                                    {/*    className={style.linkTo}*/}
                                    {/*    onClick={() => navigate('/forgot-password')}*/}
                                    {/*    size="sm"*/}
                                    {/*    fontWeight={400}*/}
                                    {/*>*/}
                                    {/*    Lupa Password?*/}
                                    {/*</Button>*/}
                                    <Button
                                    >
                                        Masuk
                                    </Button>
                            </form>
                        )}
                    </Formik>
                </div>
            </Col>
        </Row>
    );
};

export default Login;
