import React, {useRef} from "react";
import {Formik} from "formik";
import * as yup from "yup";
import emailjs from '@emailjs/browser';
import {Button, Col, Container, Form, Navbar, NavDropdown, Row} from "react-bootstrap";

import FormTextField from "../components/form/form";
import {useNavigate} from "react-router-dom";

const forgotSchema = yup.object().shape({
    email: yup.string().email("Email tidak sesuai format!").required('Email harus di isi!'),
});

const ChangePassword = () => {
    const form = useRef();
    const navigate = useNavigate();
    const serviceId = 'service_edkqgll';
    const templateId = 'template_zcvklq5';
    const userId = 'yl3YSJ8TXme_4FreF';

    const handleSubmit = (e) => {
        debugger
        e.preventDefault()
        emailjs.sendForm(serviceId, templateId, form.current, userId).then((result) => {
            debugger
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
    }

    return (
        <form ref={form} onSubmit={(e) => handleSubmit(e)}>
            <label>Name</label>
            <input type="text" name="user_name" />
            <label>Email</label>
            <input type="email" name="user_email" />
            <label>Message</label>
            <textarea name="message" />
            <input type="submit" value="Send" />
        </form>
    );
}

export default ChangePassword;
