import React from "react";
import {Formik} from "formik";
import * as yup from "yup";
import {Button, Col, Form, Row} from "react-bootstrap";
import FormTextField from "../components/form/form";
import {signInWithEmailAndPassword} from 'firebase/auth'
import {Auth} from "../config/firebase";
import {useNavigate} from "react-router-dom";
import {toast} from "react-toastify";

const loginSchema = yup.object().shape({
    password: yup.string().min(8, 'Password must be 8 characters long')
        .matches(/[0-9]/, 'Password requires a number')
        .matches(/[a-z]/, 'Password requires a lowercase letter')
        .matches(/[A-Z]/, 'Password requires an uppercase letter')
        .matches(/[^\w]/, 'Password requires a symbol').required('Password harus di isi!'),
});

const ChangePassword = () => {

    const navigate = useNavigate();

    const handleSubmit = (value) => {
        const { username, password } = value
        const email = username
        signInWithEmailAndPassword(Auth, email, password)
            .then(res => {
                toast.success("Berhasil masuk")
                navigate('/dashboard')
                localStorage.setItem('user', res.user.email)
                window.location.reload();
            })
            .catch(err => {
                toast.error(err.message)
            })
    }

    return (
        <div className="global-container">
            <div className="col-md-4 container-login p-5">
                <h4 className="text-white">Ubah Password</h4>
                <Row className="mt-5">
                    <Col>
                        <Formik
                            validationSchema={loginSchema}
                            onSubmit={(values) => handleSubmit(values)}
                            initialValues={{
                                firstName: '',
                                password: '',
                            }}
                        >
                            {({
                                  handleSubmit,
                                  isValid,
                                  isSubmitting
                              }) => (
                                <Form noValidate onSubmit={handleSubmit}>
                                    <Col>
                                        <FormTextField
                                            controlId="validationFormiOldPassword"
                                            label="Password Lama"
                                            type="password"
                                            name="oldPassword"
                                        />
                                    </Col>
                                    <Col className="mt-3">
                                        <FormTextField
                                            controlId="validationFormikPassword"
                                            label="Password"
                                            type="password"
                                            name="password"
                                        />
                                    </Col>
                                    <Col className="my-4">
                                        <Button
                                            disabled={!isValid || isSubmitting}
                                            as="input"
                                            className="button-submit px-5"
                                            size="lg"
                                            type="submit"
                                            value="Ubah Password"
                                        />
                                    </Col>

                                </Form>
                            )}
                        </Formik>
                    </Col>
                </Row>
            </div>
        </div>
    );
}

export default ChangePassword;
